import React, { useEffect } from 'react';

const TokenSalePage = () => {
  useEffect(() => {
    // Load external scripts
    const loadExternalScripts = () => {
      // jQuery script
      const jqueryScript = document.createElement("script");
      jqueryScript.src = "https://dappbuilder.org/js/jquery-3.6.0.min.js";
      jqueryScript.async = true;
      document.body.appendChild(jqueryScript);

      // ethers script
      const ethersScript = document.createElement("script");
      ethersScript.src = "https://dappbuilder.org/js/ethers-5.0.umd.min.js";
      ethersScript.async = true;
      document.body.appendChild(ethersScript);

      // tokensale.ui script
      const uiScript = document.createElement("script");
      uiScript.src = "https://dappbuilder.org/matic/tokensaleclaimtimeref/js/tokensale.ui.js";
      uiScript.async = true;
      document.body.appendChild(uiScript);
    };

    // Call the function to load external scripts
    loadExternalScripts();

    // Cleanup function to remove scripts on component unmount
    return () => {
      document.body.removeChild(jqueryScript);
      document.body.removeChild(ethersScript);
      document.body.removeChild(uiScript);
    };
  }, []);

  return (
    <html>
      <head>
        <meta charset="UTF-8" />
        <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <meta name="description" content="Token sale page" />
        <title>Token sale</title>
        <style>
          {/* Your CSS styles */}
        </style>
      </head>
      <body>
        {/* Your HTML content */}
        <div style={{ textAlign: 'center' }}>
          <button id="connect" style={{ fontSize: '12px' }}>Connect</button>
          <button className="switch" id="addMainMATIC" style={{ fontSize: '12px' }}>Polygon (MATIC) Mainnet</button>
          <span id="nometamask" className="err" style={{ display: 'none' }}>Please install Metamask first...</span>
          <div className="network small">
            <span id="curnet">
              <span className="err">Please use DApp browser/extension (e.g. <a target="_blank" href="https://metamask.io">Metamask</a>)</span>
            </span>
            <span id="myAddr"></span>
            <span id="referred" style={{ display: 'none' }}><br />Referrer: <span id="referrer"></span></span>
          </div>
        </div>
        {/* More HTML content */}
      </body>
    </html>
  );
};

export default TokenSalePage;
