import React, { useState } from "react";
import shape01 from "../../assets/img/banner/banner_shape01.png";
import shape02 from "../../assets/img/banner/banner_shape02.png";
import shape03 from "../../assets/img/banner/banner_shape03.png";
import fireIcon from "../../assets/img/icon/fire.png";
import CountDownOne from "../CountDown/CountDownOne";
const tokenName = useState('IDBit');

function TokenInfo() {
  const [tokenName, setTokenName] = useState('IDBit');
  const [tokenSymbol, setTokenSymbol] = useState('IDT');
  const [tokenAddress, setTokenAddress] = useState('0x95351dF7dC2A753C8cFa4a0fD7C3Ee9F6D883306');

  return (
      <div style={{textAlign: 'center'}}>
          <h1>Token info</h1>
          <h2>{tokenName} (<span className="tokenSymbol">{tokenSymbol}</span>)</h2>
          <p><a href={`https://polygonscan.com/token/${tokenAddress}`} target="_blank" id="tokenAddress">{tokenAddress}</a></p>
          {/* Reserved in case you want to show decimals and total supply: Decimals <span id="#tokenDecimals"></span> Total supply <span id="#tokenSupply"></span>*/}
          {/* <p><button style={{textAlign: 'center'}}>Add to Metamask</button> <button style={{textAlign: 'center'}}>Copy address</button></p> */}
      </div>
  );
}
const Banner = (props) => {
  return (
    <section className="banner-area banner-bg">
      <div className="banner-shape-wrap">
        <img src={shape01} alt="" className="img-one" />
        <img src={shape02} alt="" className="img-two" />
        <img src={shape03} alt="" className="img-three" />
      </div>

      <div className="container">
        <div className="row justify-content-center">
          <div className="col-lg-10">
            <div className="banner-content text-center">
              <img src={fireIcon} alt="" />
              <h2 className="title">
                {tokenName} <span>Crypto</span> Trading
                Strategies
              </h2>
            </div>
            <div className="banner-progress-wrap">
              <ul>
                <li>Pre Sell</li>
                <li>Soft Cap</li>
                <li>Bonus</li>
              </ul>
              <div className="progress">
                <div
                  className="progress-bar"
                  role="progressbar"
                  style={{ width: "75%" }}
                  aria-valuenow="75"
                  aria-valuemin="0"
                  aria-valuemax="100"
                />
              </div>
              <h4 className="title">
                65% target raised <span>1 ETH = $1000 = 3177.38 CIC</span>
              </h4>
            </div>
          </div>
        </div>
        <div className="row justify-content-center">
          <div className="col-xl-10">
            <div className="banner-countdown-wrap text-center">
              <h2 className="title">ICO Will Start In..</h2>

              <CountDownOne />
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
