import React from "react";
import { useHorizontalScroll } from "../../lib/hooks/useHorizontalScroll";
import RoadmapItem from "./RoadmapItem";

const Roadmap = () => {
  const roadmap_items = [
    {
      roadmapTitle: "Q1",
      title: "Recruiting a Developer Team",
      info: [
        "Providing Knowledge About Vision and Mission to the",
        "Development Team Explaining the Road Map for Achieving",
        "Goals Provides an understanding of the benefits and advantages for developers",
      ],
    },
    {
      roadmapTitle: "Q2",
      title: "Create Smart Contract IDBit ( IDT ) V1",
      info: [
        " Create a Token Contract",
        " Create a Sale Contract",
        "Build the Ico website idbit.org",
      ],
    },

    {
      roadmapTitle: "Mid of Q4 2021",
      title: "App beta test",
      info: [
        "Public testnet launch",
        "SubQuery Network Explorer and dApp",
        "Point-in-time indexing",
      ],
    },

    {
      roadmapTitle: "Mid of Q4 2021",
      title: "Token Test",
      info: [
        "SQT token generation event",
        "Public incentivize testnet launch",
        "Data traffic insights and reporting",
      ],
    },
    {
      roadmapTitle: "Mid of Q4 2021",
      title: "Alpha Test",
      info: [
        "Launch of the SubQuery Foundation",
        "Finalise research for other Layer-1 chains",
        "Liquidity mining program",
      ],
    },
    {
      roadmapTitle: "Mid of Q4 2021",
      title: "Benefits",
      info: [
        "Mainnet launch",
        "Centralized exchange launch",
        "Public incentivize testnet",
      ],
    },
    {
      roadmapTitle: "Mid of Q4 2021",
      title: "Operational",
      info: [
        <>
          SubQuery launches its <br /> own parthian
        </>,
        "SubQuery Foundation",
      ],
    },
  ];

  const scrollRef = useHorizontalScroll();

  return (
    <section id="roadmap" className="roadmap-area pt-130 pb-130">
      <div className="container custom-container-two">
        <div className="row justify-content-center">
          <div className="col-xl-5 col-lg-8">
            <div className="section-title text-center mb-60">
              <span className="sub-title">OUR Roadmap</span>
              <h2 className="title">
                Bigtech Strategy and <br /> Project <span>Plan</span>
              </h2>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-lg-12">
            <div
              className="bt-roadmap_x bt-roadmap-scrollbar"
              ref={scrollRef}
              style={{ overflow: "auto" }}
            >
              <div className="bt-roadmap-wrap">
                {roadmap_items.map((x, index) => (
                  <RoadmapItem key={index} item={x} />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Roadmap;
