import React from "react";
import { useHorizontalScroll } from "../../lib/hooks/useHorizontalScroll";
import RoadmapItem from "./RoadmapItem";

const Roadmap = () => {
  const roadmap_items = [
    {
      roadmapTitle: "Q1 ",
      title: "Recruiting a Developer Team",
      info: [
        <>
        <li>1. Providing Knowledge About Vision and Mission to the</li>
        <li>Development Team Explaining the Road Map for Achieving</li>
        <li>Goals Provides an understanding of the benefits and advantages for developers</li>
        </>
      ],
    },
    {
      roadmapTitle: "Q2",
      title: "Create Smart Contract IDBit ( IDT ) V1",
      info: [
        " Create a Token Contract",
        " Create a Sale Contract",
        "Build the Ico website idbit.org",
      ],
    },

    {
      roadmapTitle: "Q3",
      title: "Fundraising to Finance the Project",
      info: [
        "Conducted First Token Sale of 10,000,000 IDT to Initial Investors (Full Explanation) ",
        "Selling IDT Tokens to the General Public on the idbit.org Site for IDR 70,000,000 (Full Explanation) ",
        "Closes Sale and Starts Adding Liquidity in Global Markets",
        "Distributing IDT in the Global Market",
        "Branding and Special Promotions",
        "Manage Legality and Community Representative Offices"
      ],
    },

    {
      roadmapTitle: "Q4",
      title: "Building Advanced Projects",
      info: [
        "Launching App Exchange Application ",
        "Building a Token Generation Project ",
        "Building a Public Voting Application",
      ],
    },
  ];

  const scrollRef = useHorizontalScroll();

  return (
    <section id="roadmap" className="roadmap-area pt-130 pb-130">
      <div className="container custom-container-two">
        <div className="row justify-content-center">
          <div className="col-xl-5 col-lg-8">
            <div className="section-title text-center mb-60">
              <span className="sub-title">OUR Roadmap</span>
              <h2 className="title">
                Bigtech Strategy and <br /> Project <span>Plan</span>
              </h2>
            </div>
          </div>
        </div>

        <div className="row">
          <div className="col-lg-12">
            <div
              className="bt-roadmap_x bt-roadmap-scrollbar"
              ref={scrollRef}
              style={{ overflow: "auto" }}
            >
              <div className="bt-roadmap-wrap">
                {roadmap_items.map((x, index) => (
                  <RoadmapItem key={index} item={x} />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Roadmap;
